import React from'react';
import { useState } from 'react';
import './Field.css'





const FieldPassword= () => {
  const [password, setPassword] = useState(" ");    
  const[passwordError, setPasswordError] = useState('')

  const passwordHandler = (e) =>{
      setPassword(e.target.value)
      const re = /^(?=.*[A-Z])(?=.*[!@&*])(?=.*[0-9]).{8,}$/g
      if (!re.test(String(e.target.value).toLowerCase())){
          setPasswordError('Incorrect data')
      } else setPasswordError('')
  
   }
    return(
      <form action="#">
      <label class='text-field'>
        <input  class="text-field__input" placeholder="Enter your password" value={password} onChange={e => passwordHandler(e)}  value={password} onChange={e => passwordHandler(e)}  
                                                                       
                                                                          type="text" id="password" name="password"/>
        <span class="text-field__label">
         Paasword
        </span>
        {passwordError && <div className='input__error'>{passwordError}</div>}
      </label>
    </form>
    );
}

export default FieldPassword;
