import React from'react';
import { useState } from 'react';
import './Field.css'





const Field= () => {
  const[email, setEmail] = useState('')    
  const[emailError, setEmailError] = useState('')

  const emailHandler = (e) =>{
      setEmail(e.target.value)
      const re = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/  
      if (!re.test(String(e.target.value).toLowerCase())){
          setEmailError('Incorrect data')
      } else setEmailError('')
  
   }
    return(
      <form action="#">
      <label class='text-field'>
      {emailError && <div className='input__error'>{emailError}</div>}
        <input name="email-example" type="text" class="text-field__input" placeholder="Enter your E-Mail" value={email} onChange={e => emailHandler(e)}/>
        <span class="text-field__label">
          E-Mail
        </span>
      </label>
    </form>
    );
}

export default Field;
