import React from'react';
import { useState } from 'react';
import './Field.css'





const FieldPassword= () => {
  const[email, setEmail] = useState('')    
  const[emailError, setEmailError] = useState('')

  const emailHandler = (e) =>{
      setEmail(e.target.value)
      const re = /(?=.*[0-9])(?=.*[!@#$%^&*])(?=.*[A-Z].*[A-Z])[0-9A-Z!@#$%^&*]{8,}/g 
      if (!re.test(String(e.target.value).toLowerCase())){
          setEmailError('Incorrect data')
      } else setEmailError('')
  
   }
    return(
      <form action="#">
      <label class='text-field'>
        <input  class="text-field__input" placeholder="Enter your password" value={email} onChange={e => emailHandler(e)}  value={email} onChange={e => emailHandler(e)} type="text" id="email" name="email"/>
        <span class="text-field__label">
        Password
        </span>
        {emailError && <div className='input__error'>{emailError}</div>}
      </label>
    </form>
    );
}

export default FieldPassword;
