import React from'react';
import { useState } from 'react';
import './Field.css'





const FieldEmail= () => {
  const[password, setEmail] = useState('')    
  const[emailError, setEmailError] = useState('')
 


  const passwordHandler = (e) =>{
      setPassword(e.target.value)
      const re = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/  
      if (!re.test(String(e.target.value).toLowerCase())){
          setPasswordError('Incorrect data')
      } else sePasswordError('')
  
   }
    return(
      <form action="#">
      <label class='text-field'>
        <input  class="text-field__input" placeholder="Enter your password" value={password} onChange={e => passwordHandler(e)}  
                                                                       
                                                                          type="text" id="password" name="password"/>
        <span class="text-field__label">
          Password
        </span>
        {emailError && <div className='input__error'>{emailError}</div>}
      </label>
    </form>
    );
}

export default FieldEmail;
