import React from'react';
import { useState } from 'react';
import './Field.css'





const FieldPassword= (state) => {
  const [password, setPassword] = useState(state.password || "");    
  const[PasswordError, setPasswordError] = useState('')

  const emailHandler = (e) =>{
      setPassword(e.target.value)
      const re = /^(?=.*[A-Z])(?=.*[!@&*])(?=.*[0-9]).{8,}$/g
      if (!re.test(String(e.target.value).toLowerCase())){
          setPasswordError('Incorrect data')
      } else setPasswordError('')
  
   }
    return(
      <form action="#">
      <label class='text-field'>
        <input  class="text-field__input" placeholder="Enter your password" value={password} onChange={e => emailHandler(e)}  value={password} onChange={e => emailHandler(e)}
                                                                            onChange={(e) => setPassword(e.target.value)}
                                                                            defaultValue={password}
                                                                            type="text" id="" name="email"/>
        <span class="text-field__label">
        Password
        </span>
        {PasswordError && <div className='input__error'>{PasswordError}</div>}
      </label>
    </form>
    );
}

export default FieldPassword;
